import React , { Component } from 'react';

class LoginForm extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      value:"event",
      numberforevent:1
    };

    this.handleChange = this.handleChange.bind(this);

  }

  handleChange(event) {
    this.setState({value: event.target.value});
    this.setState({numberforevent:event.target.numberforevent});
  }

  render(){
    let val
    if(this.state.value=="event"){
      val=<div>
            <form >
              <label>
         Number for event:
         <input
           name="numberforevent"
           type="number"
           value={this.state.numberforevent}
           onChange={this.handleChange} />
       </label>
      </form>
      </div>
    }
    return(
      <form>
        <label>
          Pick your option:
           <select value={this.state.value} onChange={this.handleChange}>
            <option value="allevent">people of all events</option>
            <option value="event">particular event peoples</option>
            <option value="single">single person</option>
          </select>
      </label>
      <div>{val}</div>
      </form>
    )
    
  }
};

export default LoginForm;
