import React,{Component} from 'react';
import { Button } from 'reactstrap';
import './FooterComp.css';

class Footer extends Component {

    constructor(props){
        super(props);
        this.handleScrollToStats=this.handleScrollToStats.bind(this);
    }
    
    handleScrollToStats = () => {
        window.scrollTo({
            top: 0
        })
   }

   render(){
    return(
        <div className="footer">
            <div className="container">
                 
                <div className="row align-self-center">
                    <div className="button-top">
                        <Button outline onClick={this.handleScrollToStats} style={{color:"#f09103"}}>Back To Top</Button>
                    </div>
                </div>

                <div className="row" >
                    <div className="col-12 align-self-center">
                        <div className="text-center">
                        <a href="www.facebook.com">
                            <i className="fa fa-facebook-square" style={{color:"white",fontSize:"2em"}}></i>
                        </a>
                        <a href="www.instagram.com">
                            <i className="fa fa-instagram" style={{fontSize:'2em'}}></i>
                        </a>
                        <a href="www.linkedin.com">
                            <i className="fa fa-linkedin" style={{fontSize:'2em'}}></i>
                        </a>
                        <a href="www.youtube.com">
                            <i className="fa fa-youtube" style={{fontSize:'2em'}}></i>
                        </a>
                        </div> 
                    </div>
                </div>  
    
                    <div className="col-10 col-sm-5">
                        <div className="text-center">
                            <h5>Contact Us</h5>
                            <address>
                              <i className="fa fa-phone fa-lg">  fdsl flkjs : </i><a href="callto:+852 1234 5678"><h5>+852 1234 5678</h5></a><br />
                              <i className="fa fa-phone fa-lg">  fdsl flkjs : </i><a href="callto:+852 1234 5678"><h5>+852 1234 5678</h5></a><br />
                            </address>
                        </div>    
                    </div>
                
                <div className="row justify-content-center">             
                    <div className="col-auto">
                        <p>©</p>
                    </div>
                </div>
    
                
            </div>
        </div>
        );
   }
    
}

export default Footer;
