import React,{Component} from 'react';
import { Button } from 'reactstrap';
import './FooterComp.css';

class Footer extends Component {

    constructor(props){
        super(props);
        this.handleScrollToStats=this.handleScrollToStats.bind(this);
    }
    
    handleScrollToStats = () => {
        window.scrollTo({
            top: 0
        })
   }

   render(){
    return(
        <div className="footer">
            <div className="container">
                 
                <div className="row align-self-center">
                    <div className="button-top">
                        <Button outline onClick={this.handleScrollToStats} style={{color:"#f09103"}}>Back To Top</Button>
                    </div>
                </div>
                    
                    <div className="col-12 col-sm-4 align-self-center">
                        <div className="text-center">
                            <a className="btn btn-social-icon btn-google" href="http://google.com/+"><i className="fa fa-google-square"></i></a>
                            <a className="btn btn-social-icon btn-facebook" href="http://www.facebook.com/profile.php?id="><i className="fa fa-facebook"></i></a>
                            <a className="btn btn-social-icon btn-linkedin" href="http://www.linkedin.com/in/"><i className="fa fa-linkedin"></i></a>
                            <a className="btn btn-social-icon btn-twitter" href="http://twitter.com/"><i className="fa fa-twitter"></i></a>
                            <a className="btn btn-social-icon btn-google" href="http://youtube.com/"><i className="fa fa-youtube"></i></a>
                        </div>
                    </div>
    
                    <div className="col-10 col-sm-5">
                        <div className="text-center">
                            <h5>Contact Us</h5>
                            <address>
                              <i className="fa fa-phone fa-lg">  fdsl flkjs : </i><a href="callto:+852 1234 5678"><h5>+852 1234 5678</h5></a><br />
                              <i className="fa fa-phone fa-lg">  fdsl flkjs : </i><a href="callto:+852 1234 5678"><h5>+852 1234 5678</h5></a><br />
                            </address>
                        </div>    
                    </div>
                
                <div className="row justify-content-center">             
                    <div className="col-auto">
                        <p>Â©</p>
                    </div>
                </div>
    
                
            </div>
        </div>
        );
   }
    
}

export default Footer;
