import React,{ Component } from "react";


class App extends Component{
  constructor(){
    super();
    this.state={
      searchfield:""
    }
  }
  handleChange = (event) =>{
    this.setState=({searchfield:event.target.value})
  }
  render(){
    return( 
      <div className="App">
        <div className="searchbox">
          <input type='search' className='search' placeholder="type your fav colour" onChange={this.handleChange} style={{width:1000}}></input>
        </div>

        {this.state.searchfield==="blue"?(<div style={{backgroundColor: "blue"}}> <h1>you choosed blue</h1></div>)
        :this.state.searchfield===""?(<div style={{backgroundColor:"white"}}> <h1>type ur fav colour</h1></div>)
        :this.state.searchfield==="red"?(<div style={{backgroundColor:"red"}}><h1>red!!!!!!1</h1></div>)
        :null}
      </div>
    );
  }
}
export default App;
