import React , { Component } from 'react';

class LoginForm extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      value:"allevent",
      numberforevent:1,
      Email: ""
    };

    this.handleChange = this.handleChange.bind(this);

  }

  handleChange(event) {
    this.setState({value: event.target.value});
    this.setState({numberforevent:event.target.numberforevent});
    this.setState({Email:event.target.Email});
  }

  render(){
    let val
    if(this.state.value=="event"){
      val=<div>
            <form >
              <label>
         Number of that event :-
         <input
           name="numberforevent"
           type="number"
           value={this.state.numberforevent}
           onChange={this.handleChange} />
       </label>
      </form>
      </div>
    }else if(this.state.value=="single"){
      val=<div>
        <form>
          <label>
              Email of that person :-
              <input
                 name="Email"
                 type="email"
                 val={this.state.Email}
                 onchange={this.handleChange}/>
          </label>
        </form>
      </div>
    }
    return(
      <form>
        <label>
          Pick your option :-
           <select value={this.state.value} onChange={this.handleChange}>
            <option value="allevent">people of all events</option>
            <option value="event">particular event peoples</option>
            <option value="single">single person</option>
          </select>
      </label>
      <div>{val}</div>
      </form>
    )
    
  }
};

export default LoginForm;
