import React,{ Component }  from 'react';
import {Card,CardImg,CardText,CardBody,CardTitle,Breadcrumb,BreadcrumbItem,Button,
    Modal,ModalBody,ModalHeader,Label, FormGroup} from 'reactstrap';
import {Link} from 'react-router-dom';
import { Control, Errors, LocalForm } from 'react-redux-form';
import { Loading } from './LoadingComponent';

    function Renderdish({dish}){
        if(dish!=null){
            return(
                <div>
                <Card key={dish.id}>
                    <CardImg width="100%" src={dish.image} alt={dish.name}/>
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
                </div>
            );
        }else{
            return(
                <div></div>
            );
        }
    }
    function Rendercomment({comments,addComment,dishId}){
       
            const com=comments.map((comment)=>{
                return(
                    <Card>
                        <li key={comment.id}>
                        <p>{comment.comment}</p>
                        <p>-- {comment.author} , {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(comment.date)))}</p>
                        </li>
                    </Card>
                );
            })
            return(
                <div>
                    <h2>comments</h2>
                    {com}
                    <CommentForm dishId={dishId} addComment={addComment}/>
                </div>
            );
        

    }
    const Dishdetail = (props) => {
            if(props.isLoading){
                return(
                    <div className="container">
                        <div className="row">
                            <Loading />
                        </div>
                    </div>
                )
            }
            else if(props.errMess){
                return(
                    <div className="container">
                        <div className="row">
                            <h4>{props.errMess}</h4>
                        </div>
                    </div>
                )
            }
            else if(props.selectedDish!=null){
                return(
                    <div className="container">
                        <div className="row">
                        <Breadcrumb>
                            <BreadcrumbItem><Link to='/menu'>Menu</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.selectedDish.name}</BreadcrumbItem>
                        </Breadcrumb>
                        <div className="col-12">
                            <h3>Menu</h3>
                            <hr/>
                        </div>
                            <div className="col-12 col-md-5 m-1">
                                <Renderdish dish={props.selectedDish}/>
                            </div>
                            <div className="col-12 col-md-5 m-1">
                                <Rendercomment comments={props.comments}
                                  addComment={props.addComment}
                                  dishId={props.selectedDish.id}/>
                            </div>
                        </div>
                    </div>
                );
                
            }else{
                return(
                    <div>

                    </div>
                );
            }
             
    }

const required = (val) => val && val.length;
const maxLength = (len) => (val) => !(val) || (val.length) <= len;
const minLength = (len) => (val) => (val) && (val.length) >= len;

class CommentForm extends Component{

    constructor(props){
        super(props);
        this.state={
            isModalOpen:false
        };
        this.toggleModal=this.toggleModal.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);

    }

    toggleModal(){
        this.setState({
            isModalOpen:!this.state.isModalOpen
        });
    }
    
    handleSubmit(values){
        this.props.addComment(this.props.dishId,values.rating,values.author,values.comment)
        this.toggleModal();
    }
    render(){
        return(
            <React.Fragment>
                <Button outline onClick={this.toggleModal}>
                    <span className="submit" color="primary"></span>Submit
                </Button>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                    <ModalBody>
                    <LocalForm onSubmit={(values)=>this.handleSubmit(values)}>
                        <FormGroup>
                            <Label htmlFor="rating">Rating</Label>
                                <Control.select model=".rating" name="rating"
                                    className="form-control">
                                        <option>1</option>
                                        <option>2</option>
                                        <option>3</option>
                                        <option>4</option>
                                        <option>5</option>
                                </Control.select>
                        </FormGroup>
                        <FormGroup>
                            <Label htmlfor="author" >Your Name</Label>
                                <Control.text model=".author" id="author" name="author"
                                placeholder="Your Name"
                                className="form-control"
                                validators={{
                                    required,minLength : minLength(3),maxLength :  maxLength(15)
                                }}
                                />
                                <Errors
                                    className="text-danger"
                                    model=".yourname"
                                    show="touched"
                                    messages={{
                                        minLength:'Must be greater than 2 char',
                                        maxLength : 'must be less than 15 char'
                                    }}
                                />
                        </FormGroup>
                        <FormGroup>
                            <Label htmlfor="comment" >Comment</Label>
                                <Control.textarea model=".comment" id="comment" name="comment"
                                rows="6"
                                placeholder="Comment"
                                className="form-control" />
                        </FormGroup>
                        <FormGroup>
                                <Button type="submit" color="primary">
                                    Submit
                                </Button>
                        </FormGroup>
                    </LocalForm>
                    </ModalBody>
                </Modal>
        </React.Fragment>
        );
    }
}

export default Dishdetail;
