import React,{ Component }  from 'react';
import {Card,CardImg,CardText,CardBody,CardTitle,Breadcrumb,BreadcrumbItem,Button,
    Modal,ModalBody,ModalHeader,Row,Label,Col} from 'reactstrap';
import {Link} from 'react-router-dom';
import { Control, Errors, LocalForm } from 'react-redux-form';

    function Renderdish({dish}){
        if(dish!=null){
            return(
                <div>
                <Card key={dish.id}>
                    <CardImg width="100%" src={dish.image} alt={dish.name}/>
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
                </div>
            );
        }else{
            return(
                <div></div>
            );
        }
    }
    function Rendercomment({comments}){
       
            const com=comments.map((comment)=>{
                return(
                    <Card>
                        <li key={comment.id}>
                        <p>{comment.comment}</p>
                        <p>-- {comment.author} , {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(comment.date)))}</p>
                        </li>
                    </Card>
                );
            })
            return(
                <div>
                    <h2>comments</h2>
                    {com}
                    <CommentForm/>
                </div>
            );
        

    }
    const Dishdetail = (props) => {
            if(props.selectedDish!=null){
                return(
                    <div className="container">
                        <div className="row">
                        <Breadcrumb>
                            <BreadcrumbItem><Link to='/menu'>Menu</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.selectedDish.name}</BreadcrumbItem>
                        </Breadcrumb>
                        <div className="col-12">
                            <h3>Menu</h3>
                            <hr/>
                        </div>
                            <div className="col-12 col-md-5 m-1">
                                <Renderdish dish={props.selectedDish}/>
                            </div>
                            <div className="col-12 col-md-5 m-1">
                                <Rendercomment comments={props.comments}/>
                            </div>
                        </div>
                    </div>
                );
                
            }else{
                return(
                    <div>

                    </div>
                );
            }
             
    }

const maxLength = (len) => (val) => !(val) || (val.length) <= len;
const minLength = (len) => (val) => (val) && (val.length) >= len;

class CommentForm extends Component{

    constructor(props){
        super(props);
        this.state={
            isModalOpen:false
        };
        this.toggleModal=this.toggleModal.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);

    }

    toggleModal(){
        this.setState({
            isModalOpen:!this.state.isModalOpen
        });
    }
    
    handleSubmit(values){
        this.toggleModal();
        console.log("current state is :"+JSON.stringify(values));
        alert("current state is :"+JSON.stringify(values));
    }
    render(){
        return(
            <React.Fragment>
                <Button outline onClick={this.toggleModal}>
                    <span className="submit" color="primary"></span>Submit
                </Button>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                    <ModalBody>
                    <LocalForm onSubmit={(values)=>this.handleSubmit(values)}>
                        <Row className="form-group">
                            <Label htmlFor="rating">Rating</Label>
                                <Control.number modal=".rating" id="rating" name="rating"
                                placeholder="1"
                                className="form-control"
                                />
                        </Row>
                        <Row className="form-group">
                            <Label htmlfor="yourname" md={2}>Your Name</Label>
                                <Control.text model=".yourname" id="yourname" name="yourname"
                                placeholder="Your Name"
                                className="form-control"
                                validators={{
                                    minLength : minLength(3),maxLength :  maxLength(10)
                                }}
                                />
                                <Errors
                                    className="text-danger"
                                    model=".lastname"
                                    show="touched"
                                    messages={{
                                        minLength:'Must be greater than 2 char',
                                        maxLength : 'must be less than 10 char'
                                    }}
                                />
                        </Row>
                        <Row className="form-group">
                            <Label htmlfor="message" md={2}>Comment</Label>
                            <Col md={10}>
                                <Control.textarea model=".message" id="message" name="message"
                                rows="6"
                                placeholder="Comment"
                                className="form-control" />
                            </Col>
                        </Row>
                        <Row className="form-group">
                                <Button type="submit" color="primary">
                                    Submit
                                </Button>
                        </Row>
                    </LocalForm>
                    </ModalBody>
                </Modal>
        </React.Fragment>
        );
    }
}

export default Dishdetail;
