import React , { Component } from 'react';

class LoginForm extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      value:"allevent",
      numberforevent:1
    };

    this.handleChange = this.handleChange.bind(this);

  }

  handleChange(event) {
    this.setState({value: event.target.value});
    this.setState({numberforevent:event.target.numberforevent});
  }

  render(){
     return(
      <form >
        
      <label>
         Number for event:
         <input
           name="numberforevent"
           type="number"
           value={this.state.numberforevent}
           onChange={this.handleChange} />
       </label>
      </form>
      )
    
  }
};

export default LoginForm;
